import React, { useEffect, useState } from 'react';
import TaskList from './TaskList';
import TaskForm from './TaskForm';
import SearchBar from './SearchBar';

function TaskManager() {
  const [tasks, setTasks] = useState([]);
  const [editTask, setEditTask] = useState(null); // если не null, значит редактируем
  const [searchQuery, setSearchQuery] = useState('');

  // ВАЖНО: меняем порт на 5000 (бэкенд), а не 3000.
  // Если используете Docker Compose, оставьте process.env.REACT_APP_API_URL.
  const backendUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';

  // Загрузка списка (GET /tasks/)
  const fetchTasks = async () => {
    try {
      const response = await fetch(`${backendUrl}/tasks/`);
      const data = await response.json();
      setTasks(data);
    } catch (error) {
      console.error('Ошибка при загрузке задач:', error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Создать задачу (POST /tasks/)
  const handleCreate = async (title, description, username) => {
    try {
      const response = await fetch(`${backendUrl}/tasks/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title, description, username })
      });
      const data = await response.json();
      if (response.ok) {
        // Успешно создали
        fetchTasks(); // обновляем список
      } else {
        console.error('Ошибка при создании задачи:', data.error);
      }
    } catch (error) {
      console.error('Ошибка при создании задачи:', error);
    }
  };

  // Обновить задачу (PUT /tasks/:id)
  const handleUpdate = async (id, title, description) => {
    try {
      const response = await fetch(`${backendUrl}/tasks/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title, description })
      });
      const data = await response.json();
      if (response.ok) {
        fetchTasks();
      } else {
        console.error('Ошибка при обновлении задачи:', data.error);
      }
    } catch (error) {
      console.error('Ошибка при обновлении задачи:', error);
    }
  };

  // Удалить задачу (DELETE /tasks/:id)
  const handleDelete = async (id) => {
    try {
      const response = await fetch(`${backendUrl}/tasks/${id}`, {
        method: 'DELETE'
      });
      const data = await response.json();
      if (response.ok) {
        fetchTasks();
      } else {
        console.error('Ошибка при удалении задачи:', data.error);
      }
    } catch (error) {
      console.error('Ошибка при удалении задачи:', error);
    }
  };

  // Поиск (GET /tasks/search?q=...)
  const handleSearch = async (query) => {
    setSearchQuery(query);
    if (!query) {
      // Если строка поиска пустая, грузим все
      fetchTasks();
      return;
    }
    try {
      const response = await fetch(`${backendUrl}/tasks/search?q=${query}`);
      if (response.ok) {
        const data = await response.json();
        setTasks(data);
      } else {
        console.error('Ошибка при поиске задач:', response.status);
      }
    } catch (error) {
      console.error('Failed to fetch:', error);
    }
  };

  // Начать редактирование
  const handleEditClick = (task) => {
    setEditTask(task);
  };

  const handleSaveEdit = (id, newTitle, newDescription) => {
    handleUpdate(id, newTitle, newDescription);
    setEditTask(null);
  };

  const handleCancelEdit = () => {
    setEditTask(null);
  };

  return (
    <div>
      <SearchBar onSearch={handleSearch} />
      <TaskForm
        onCreate={handleCreate}
        editTask={editTask}
        onSaveEdit={handleSaveEdit}
        onCancelEdit={handleCancelEdit}
      />
      <TaskList
        tasks={tasks}
        onEditClick={handleEditClick}
        onDeleteClick={handleDelete}
      />

      {searchQuery && (
        <p style={{ marginTop: '10px' }}>
          Результаты поиска по: "{searchQuery}"
        </p>
      )}
    </div>
  );
}

export default TaskManager;
