import React, { useState, useEffect } from 'react';

function TaskForm({ onCreate, editTask, onSaveEdit, onCancelEdit }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [username, setUsername] = useState('');

  useEffect(() => {
    if (editTask) {
      setTitle(editTask.title);
      setDescription(editTask.description || '');
    } else {
      setTitle('');
      setDescription('');
      setUsername('');
    }
  }, [editTask]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editTask) {
      onSaveEdit(editTask.id, title, description);
    } else {
      // username не используется в бэкенде,
      // но оставим для примера
      onCreate(title, description, username);
    }
  };

  return (
    <div style={{ marginTop: '20px' }}>
      {editTask ? <h3>Редактировать задачу</h3> : <h3>Создать задачу</h3>}
      <form onSubmit={handleSubmit}>
        <label>Название:</label>
        <br />
        <input
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <br />

        <label>Описание:</label>
        <br />
        <input
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <br />

        {/* Только при создании */}
        {!editTask && (
          <>
            <label>Пользователь:</label>
            <br />
            <input
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="(Необязательно)"
            />
            <br />
          </>
        )}

        <button type="submit">
          {editTask ? 'Сохранить' : 'Создать'}
        </button>
        {editTask && (
          <button
            type="button"
            onClick={onCancelEdit}
            style={{ marginLeft: '10px' }}
          >
            Отмена
          </button>
        )}
      </form>
    </div>
  );
}

export default TaskForm;
