import React, { useState } from "react";

export default function TaskList({ tasks, onTaskDeleted, onTaskUpdated }) {
    const [editingTaskId, setEditingTaskId] = useState(null);
    const [editedTitle, setEditedTitle] = useState("");
    const [editedDescription, setEditedDescription] = useState("");

    const handleDelete = (taskId) => {
        if (window.confirm("Are you sure you want to delete the task?")) {
            onTaskDeleted(taskId);
        }
    };

    const handleEditClick = (task) => {
        setEditingTaskId(task.id);
        setEditedTitle(task.title);
        setEditedDescription(task.description);
    };

    const handleCancelEdit = () => {
        setEditingTaskId(null);
        setEditedTitle("");
        setEditedDescription("");
    };

    const handleSaveEdit = () => {
        onTaskUpdated(editingTaskId, { title: editedTitle, description: editedDescription });
        setEditingTaskId(null);
    };

    return (
        <div>
            <h2>Ваши задачи</h2>
            <ul>
                {tasks.map((task) => (
                    <li key={task.id}>
                        {editingTaskId === task.id ? (
                            <div>
                                <input
                                    type="text"
                                    value={editedTitle}
                                    onChange={(e) => setEditedTitle(e.target.value)}
                                />
                                <textarea
                                    value={editedDescription}
                                    onChange={(e) => setEditedDescription(e.target.value)}
                                ></textarea>
                                <button onClick={handleSaveEdit}>Сохранить</button>
                                <button onClick={handleCancelEdit}>Отмена</button>
                            </div>
                        ) : (
                            <div>
                                <strong>{task.title}</strong>
                                <p>{task.description}</p>
                                {task.user_id && <p>User ID: {task.user_id}</p>}
                                <p>Дата создания: {new Date(task.created_at).toLocaleString()}</p>
                                <button onClick={() => handleEditClick(task)}>Редактировать</button>
                                <button onClick={() => handleDelete(task.id)}>Удалить</button>
                            </div>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}
