import React, { useState, useEffect } from "react";
import TaskList from "./TaskList";
import TaskForm from "./TaskForm";
import UserForm from "./UserForm";

export default function TaskManager() {
    const [tasks, setTasks] = useState([]);
    const [users, setUsers] = useState([]);

    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        try {
            const response = await fetch("http://localhost:5000/tasks/");
            const data = await response.json();
            setTasks(data);
        } catch (error) {
            console.error("Error when loading tasks:", error);
        }
    };

    const onTaskCreated = (task) => {
        setTasks([...tasks, task]);
    };

    const onUserCreated = (user) => {
        setUsers([...users, user]);
        alert(`Пользователь ${user.username} успешно создан!`);
    };

    const onTaskDeleted = async (taskId) => {
        try {
            const response = await fetch(`http://localhost:5000/tasks/${taskId}`, {
                method: "DELETE",
            });

            if (response.ok) {
                setTasks(tasks.filter((task) => task.id !== taskId));
                alert("Задача успешно удалена!");
            } else {
                const data = await response.json();
                alert(`Ошибка при удалении задачи: ${data.error}`);
            }
        } catch (error) {
            console.error("Ошибка при удалении задачи:", error);
            alert("Не удалось удалить задачу.");
        }
    };

    return (
        <div>
            <h1>Менеджер задач</h1>
            <UserForm onUserCreated={onUserCreated} />
            <TaskForm onTaskCreated={onTaskCreated} />
            <TaskList tasks={tasks} onTaskDeleted={onTaskDeleted} />
        </div>
    );
}
