import React from 'react';

function TaskList({ tasks, onEditClick, onDeleteClick }) {
  return (
    <div className="task-list">
      <h3>Список задач</h3>
      {tasks.length === 0 ? (
        <p>Нет задач</p>
      ) : (
        <ul>
          {tasks.map((task) => (
            <li key={task.id}>
              <div>
                <strong>{task.title}</strong> (ID: {task.id})
                <br />
                {task.description && <em>{task.description}</em>}
                <br />
                Пользователь: {task.username || task.user_id}
              </div>
              <div>
                <button onClick={() => onEditClick(task)}>Редактировать</button>
                <button onClick={() => onDeleteClick(task.id)} style={{ marginLeft: '10px' }}>
                  Удалить
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default TaskList;
