import React, { useState } from "react";

export default function TaskList({ tasks, onTaskDeleted, onTaskUpdated }) {
    const [editingTask, setEditingTask] = useState(null);
    const [editedTitle, setEditedTitle] = useState("");
    const [editedDescription, setEditedDescription] = useState("");

    const handleDelete = async (taskId) => {
        if (window.confirm("–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å –∑–∞–¥–∞—á—É?")) {
            await onTaskDeleted(taskId);
        }
    };

    const handleEdit = (task) => {
        setEditingTask(task.id);
        setEditedTitle(task.title);
        setEditedDescription(task.description);
    };

    const handleSave = async (taskId) => {
        if (!editedTitle.trim()) {
            alert("–ù–∞–∑–≤–∞–Ω–∏–µ –∑–∞–¥–∞—á–∏ –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –ø—É—Å—Ç—ã–º!");
            return;
        }

        await onTaskUpdated(taskId, editedTitle, editedDescription);
        setEditingTask(null); // –í—ã—Ö–æ–¥–∏–º –∏–∑ —Ä–µ–∂–∏–º–∞ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
    };

    return (
        <div>
            <h2>–°–ø–∏—Å–æ–∫ –∑–∞–¥–∞—á</h2>
            <ul>
                {tasks.map((task) => (
                    <li key={task.id}>
                        {editingTask === task.id ? (
                            <>
                                <input
                                    type="text"
                                    value={editedTitle}
                                    onChange={(e) => setEditedTitle(e.target.value)}
                                    placeholder="–ù–∞–∑–≤–∞–Ω–∏–µ –∑–∞–¥–∞—á–∏"
                                />
                                <textarea
                                    value={editedDescription}
                                    onChange={(e) => setEditedDescription(e.target.value)}
                                    placeholder="–û–ø–∏—Å–∞–Ω–∏–µ –∑–∞–¥–∞—á–∏"
                                ></textarea>
                                <button onClick={() => handleSave(task.id)}>üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å</button>
                                <button onClick={() => setEditingTask(null)}>‚ùå –û—Ç–º–µ–Ω–∞</button>
                            </>
                        ) : (
                            <>
                                <strong>{task.title}</strong>
                                <p>{task.description}</p>
                                {task.user_id && <p>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å ID: {task.user_id}</p>}
                                <p>–î–∞—Ç–∞ —Å–æ–∑–¥–∞–Ω–∏—è: {new Date(task.created_at).toLocaleString()}</p>
                                <button onClick={() => handleEdit(task)}>‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å</button>
                                <button onClick={() => handleDelete(task.id)}>üóëÔ∏è –£–¥–∞–ª–∏—Ç—å</button>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}
