import React from "react";

export default function TaskList({ tasks, onTaskDeleted }) {
    const handleDelete = (taskId) => {
        if (window.confirm("Вы уверены, что хотите удалить задачу?")) {
            onTaskDeleted(taskId);
        }
    };

    return (
        <div>
            <h2>Список задач</h2>
            <ul>
                {tasks.map((task) => (
                    <li key={task.id}>
                        <strong>{task.title}</strong>
                        <p>{task.description}</p>
                        {task.user_id && <p>Пользователь ID: {task.user_id}</p>}
                        <p>Дата создания: {new Date(task.created_at).toLocaleString()}</p>
                        <button onClick={() => handleDelete(task.id)}>Удалить</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}
