import React from "react";

export default function TaskList({ tasks, onTaskDeleted }) {
    const handleDelete = async (taskId) => {
        try {
            const response = await fetch(`http://localhost:5000/tasks/${taskId}`, {
                method: "DELETE",
            });

            if (response.ok) {
                alert("Задача удалена!");
                onTaskDeleted(taskId); // Уведомляем родительский компонент
            } else {
                const data = await response.json();
                alert(`Ошибка при удалении задачи: ${data.error}`);
            }
        } catch (error) {
            console.error("Ошибка при удалении задачи:", error);
            alert("Не удалось удалить задачу.");
        }
    };

    return (
        <div>
            <h2>Список задач</h2>
            <ul>
                {tasks.map((task) => (
                    <li key={task.id}>
                        <strong>{task.title}</strong>
                        <p>{task.description}</p>
                        {task.user_id && <p>Пользователь ID: {task.user_id}</p>}
                        <p>Дата создания: {new Date(task.created_at).toLocaleString()}</p>
                        <button onClick={() => handleDelete(task.id)}>Удалить</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}
