import React, { useState } from 'react';
import LoginForm from './LoginForm';
import RegisterForm from './RegisterForm';

function App() {
  const [page, setPage] = useState('home');

  return (
    <div>
      <div style={{ marginBottom: '20px' }}>
        <button onClick={() => setPage('home')} style={{ marginRight: '10px' }}>
          Главная
        </button>
        <button onClick={() => setPage('login')} style={{ marginRight: '10px' }}>
          Логин
        </button>
        <button onClick={() => setPage('register')}>
          Регистрация
        </button>
      </div>

      {page === 'home' && (
        <div>
          <h1>Добро пожаловать в React-Frontend</h1>
          <p>Здесь может быть описание вашего проекта, ссылки на страницы, и т.д.</p>
        </div>
      )}

      {page === 'login' && <LoginForm />}

      {page === 'register' && <RegisterForm />}
    </div>
  );
}

export default App;
