import React, { useState } from "react";

export default function TaskList({ tasks, onTaskDeleted, onTaskUpdated }) {
    const [editingTaskId, setEditingTaskId] = useState(null);
    const [editingTitle, setEditingTitle] = useState("");
    const [editingDescription, setEditingDescription] = useState("");

    const handleEdit = (task) => {
        setEditingTaskId(task.id);
        setEditingTitle(task.title);
        setEditingDescription(task.description);
    };

    const handleCancelEdit = () => {
        setEditingTaskId(null);
        setEditingTitle("");
        setEditingDescription("");
    };

    const handleSaveEdit = () => {
        onTaskUpdated(editingTaskId, editingTitle, editingDescription);
        handleCancelEdit();
    };

    return (
        <div>
            <h2>Список задач</h2>
            <ul>
                {tasks.map((task) => (
                    <li key={task.id}>
                        {editingTaskId === task.id ? (
                            <div>
                                <input
                                    type="text"
                                    value={editingTitle}
                                    onChange={(e) => setEditingTitle(e.target.value)}
                                    placeholder="Название задачи"
                                />
                                <textarea
                                    value={editingDescription}
                                    onChange={(e) => setEditingDescription(e.target.value)}
                                    placeholder="Описание задачи"
                                ></textarea>
                                <button onClick={handleSaveEdit}>Сохранить</button>
                                <button onClick={handleCancelEdit}>Отменить</button>
                            </div>
                        ) : (
                            <div>
                                <strong>{task.title}</strong>
                                <p>{task.description}</p>
                                <p>Дата создания: {new Date(task.created_at).toLocaleString()}</p>
                                <button onClick={() => handleEdit(task)}>Редактировать</button>
                                <button onClick={() => onTaskDeleted(task.id)}>Удалить</button>
                            </div>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}
